package com.syntax.class13;

public class Recap2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str = "Where is Sameer?";
		System.out.println(str.charAt(2)); // shows what is inside under that index
		System.out.println(str.indexOf(3));
		System.out.println(str.indexOf("i")); // check the index of "" (the first one)
		System.out.println(str.indexOf("e",3)); //check the index of "" (after # of characters)
	}

}
